var path = require('path');
module.exports = function(periodic){
	// express,app,logger,config,db,mongoose
	var adminRouter = periodic.express.Router(),
		authController = require('../periodicjs.ext.login/controller/auth')(periodic);

	adminRouter.get('/',authController.ensureAuthenticated,function(req,res,next){
		res.send('admin routersss');
	});

	adminRouter.get('/home',function(req,res,next){
		res.render(path.resolve(__dirname,'views/index'), { name: 'Tobi' });
	});

	periodic.app.use('/admin',adminRouter);
};