var path = require('path');
module.exports = function(pluginHost){
	// express,app,logger,config
	var adminRouter = pluginHost.express.Router();

	adminRouter.get('/',function(req,res,next){
		res.send('admin routersss');
	});

	adminRouter.get('/home',function(req,res,next){
		res.render(path.resolve(__dirname,'views/index'), { name: 'Tobi' });
	});

	pluginHost.app.use('/admin',adminRouter);
};